mapper.select:
SELECT id , service_area_id as serviceAreaId , name , s.BCID , DATE_FORMAT(start_date, '%Y-%m-%d') searchDate , DATE_FORMAT(start_date, '%Y-%m-%d %H:%i:%s') start_date , DATE_FORMAT(end_date, '%Y-%m-%d %H:%i:%s') end_date
, DATE_FORMAT(start_date , '%H') start_hour , DATE_FORMAT(start_date , '%i') start_mins , DATE_FORMAT(start_date , '%s') start_second
, DATE_FORMAT(end_date , '%H') end_hour , DATE_FORMAT(end_date , '%i') end_mins , DATE_FORMAT(end_date , '%s') end_second , b.transactionId , b.serviceId , b.service , b.service_name , b.serviceClass , b.language , b.GBR , b.QCI , b.level , b.preEmptionCapabiity , b.preEmptionVulnerability , b.fecType , b.fecRatio , b.said , DATE_FORMAT(schedule_start, '%Y-%m-%d %H:%i:%s') schedule_start , DATE_FORMAT(schedule_stop, '%Y-%m-%d %H:%i:%s') schedule_stop , b.reportType , b.offsetTime , b.randomTime , b.fileURI , DATE_FORMAT(deliveryInfo_start, '%Y-%m-%d %H:%i:%s') deliveryInfo_start , DATE_FORMAT(deliveryInfo_end, '%Y-%m-%d %H:%i:%s') deliveryInfo_end , b.mpdURI , b.samplePercentage FROM schedule s left join broadcast_info b on s.BCID = b.BCID WHERE 1=1 AND id = #{id}

mapper.update:
UPDATE schedule	SET
	start_date = #{startTime} 
	, end_date = #{endTime}
	, updated_at = now()
WHERE id = #{scheduleId}

mapper.insert:
INSERT INTO broadcast_info(transactionId
	, serviceId 
	, service
	, service_name
	, serviceClass
	, language
	, GBR
	, QCI
	, level
	, preEmptionCapabiity
	, preEmptionVulnerability
	, fecType
	, fecRatio
	, segmentAvailableOffset
	, said
	, schedule_start
	, schedule_stop
	, reportType
	, offsetTime
	, randomTime
	, fileURI
	, deliveryInfo_start
	, deliveryInfo_end
	, mpdURI
	, samplePercentage
)VALUES(#{transactionId}
	, #{serviceId}
	, #{serviceType}
	, #{name}
	, #{serviceClass}
	, #{serviceLanguage}
	, #{GBR}
	, #{QCI}
	, #{level}
	, #{preEmptionCapabiity}
	, #{preEmptionVulnerability}
	, #{fecType}
	, #{fecRatio}
	, #{segmentAvailableOffset}
	, #{said}
	, str_to_date(#{schedule_start}, '%Y%m%d%H%i%s')
	, str_to_date(#{schedule_stop}, '%Y%m%d%H%i%s')
	, #{reportType}
	, #{offsetTime}
	, #{randomTime}
	, #{fileURI}
	, str_to_date(#{deliveryInfo_start}, '%Y%m%d%H%i%s')
	, str_to_date(#{deliveryInfo_end}, '%Y%m%d%H%i%s')
	, #{mpdURI}
	, #{samplePercentage}
)

mapper.update:
UPDATE broadcast_info SET
	updated_at = now()
	, transactionId		= #{transactionId}
	
		, service_name 		= #{name}
	
	
	, serviceClass 		= #{serviceClass}
	
	
	, language			= #{serviceLanguage}
		 
	
		, GBR				= #{GBR}
	 
	
		, QCI				= #{QCI}
	
	
		, level				= #{level}
	
	
		, preEmptionCapabiity		=#{preEmptionCapabiity}
	
	
		, preEmptionVulnerability	=#{preEmptionVulnerability}
	
	
		, fecType			= #{fecType}
	
	
		, fecRatio			= #{fecRatio}
	
	
		, segmentAvailableOffset	=#{segmentAvailableOffset}
	
	
		, said				= #{said}
	
	
		, schedule_start	= str_to_date(#{schedule_start}, '%Y%m%d%H%i%s')
	
	
		, schedule_stop		= str_to_date(#{schedule_stop}, '%Y%m%d%H%i%s')
	
	
		, reportType		= #{reportType}
	
	
		, offsetTime		= #{offsetTime}
	
	
		, randomTime		= #{randomTime}
	
	
		, fileURI			= #{fileURI}
	
	
		, deliveryInfo_start= str_to_date(#{deliveryInfo_start}, '%Y%m%d%H%i%s')
	
	
		, deliveryInfo_end	= str_to_date(#{deliveryInfo_end}, '%Y%m%d%H%i%s')
	
	
		, mpdURI			= #{mpdURI}
	
	
		, samplePercentage	= #{samplePercentage} 
	
WHERE 1=1
AND BCID = #{BCID}

mapper.select:
SELECT IFNULL(sum(GBR), '0') as GBRSum FROM schedule s inner join broadcast_info b ON (s.BCID = b.BCID) WHERE 1=1
AND DATE_FORMAT(now(), '%Y%m%d%H%i%s') > DATE_FORMAT(s.start_date, '%Y%m%d%H%i%s') AND DATE_FORMAT(now(), '%Y%m%d%H%i%s') < DATE_FORMAT(s.end_date, '%Y%m%d%H%i%s')
AND service_area_id = #{serviceAreaId}
